mapper.update:
update images
set image_node_id = #{nodeId,jdbcType=BIGINT},
  image_url = #{url,jdbcType=VARCHAR},
  image_description = #{description,jdbcType=LONGVARCHAR}
where image_id = #{id,jdbcType=BIGINT}

mapper.select:
select image_id, image_node_id, image_url, image_description
from images
where image_id = #{id,jdbcType=BIGINT}
