mapper.update:
update musics
set music_node_id = #{nodeId,jdbcType=BIGINT},
  music_url = #{url,jdbcType=VARCHAR},
  music_format = #{format,jdbcType=VARCHAR}
where music_id = #{id,jdbcType=BIGINT}

mapper.select:
select music_id, music_node_id, music_url, music_format
from musics
where music_id = #{id,jdbcType=BIGINT}
