mapper.insert:
insert into parts (part_id, part_parent_id, part_content_id,
                   part_content_order
  )
values (#{id,jdbcType=BIGINT}, #{parentId,jdbcType=BIGINT}, #{contentId,jdbcType=LONGVARCHAR},
        #{contentOrder,jdbcType=INTEGER}
  )

mapper.update:
update parts
set part_parent_id = #{parentId,jdbcType=BIGINT},
    part_content_id = #{contentId,jdbcType=BIGINT},
    part_content_order = #{contentOrder,jdbcType=INTEGER}
where part_id = #{id,jdbcType=BIGINT}

mapper.select:
select count(part_id) from parts
where part_parent_id = #{id,jdbcType=BIGINT}
group by part_parent_id

