mapper.insert:
insert into texts (text_id, text_node_id, text_content
  )
values (#{id,jdbcType=BIGINT}, #{nodeId,jdbcType=BIGINT}, #{content,jdbcType=LONGVARCHAR}
  )

mapper.update:
update texts
set text_node_id = #{nodeId,jdbcType=BIGINT},
  text_content = #{content,jdbcType=LONGVARCHAR}
where text_id = #{id,jdbcType=BIGINT}

mapper.select:
select text_id, text_node_id, text_content
from texts
where text_id = #{id,jdbcType=BIGINT}
