mapper.update:
update videos
set video_node_id = #{nodeId,jdbcType=BIGINT},
  video_url = #{url,jdbcType=VARCHAR},
  video_format = #{format,jdbcType=VARCHAR}
where video_id = #{id,jdbcType=BIGINT}

mapper.select:
select video_id, video_node_id, video_url, video_format
from videos
where video_id = #{id,jdbcType=BIGINT}
